sys_script_include.script:
var NeeditUtils = Class.create();
NeeditUtils.prototype = {
    initialize: function() {
    },

	generateRequests : function(num) {
		var data = {
			'u_request_type' : ['hr', 'legal', 'facilities'],
			'u_requested_for' : [
				'681ccaf9c0a8016400b98a06818d57c7', 
				'46d44a23a9fe19810012d100cca80666', 
				'5137153cc611227c000bbd1bd8cd2005'
			],
			'u_what_needed': null,
			state: [13,13,13,3],
			u_when_needed: null
		};

		for (var x=0; x<num; x++) {
			var rec_data = {};
			var rec = new GlideRecord(gs.getCurrentScopeName()+'_needit');
			rec.initialize();
			rec.setValue('active', true);
			for (var fld in data) {
				switch (fld) {
					case 'u_when_needed':
						var when = new GlideDateTime();
						when.addDaysLocalTime( Math.floor(Math.random()*30) + 1);
						rec_data[fld] = when;
						break;
					case 'u_what_needed':
						var o = (x % 3) + 1;
						rec_data[fld] = (o==3 ? 'other' : rec_data['u_request_type']+o.toString());
						break;
					default:
						var idx = Math.floor(Math.random()*data[fld].length);
						rec_data[fld] = data[fld][idx];
				}
				rec.setValue(fld, rec_data[fld]);
			}
			var sys_id = rec.insert();
		}

	},
    type: 'NeeditUtils'
};
